import React, { Component } from 'react';
import qs from 'query-string'
import axios from 'axios';

import Html5Player from 'html5-player'

import { localStorageGet, localStorageSet } from '../../StorageGS';

import 'html5-player/libs/assets/css/style.css'

export default class index extends Component {

    // 接受参数
    componentDidMount(){
        const _this = this
        let id = qs.parse(this.props.location.search).id
        if (id !== "") {
            localStorageSet("liveId",id)
        } else {
            id = localStorageGet("liveId")
        }
        axios.get("http://localhost:3001/api/liveurl", {
            params: { id: localStorageGet("liveId") }
        })
        .then (function (response) {
            console.log(response.data.result.data.durl[3].url)
        })
        .catch (function (err) {
            console.log(err)
        })
    }

    render() {
        return (
            <div>
                <Html5Player 
                width="900px"
                height="560px"
                livingMaxBuffer={ 5 }
                file="http://cn-jsnt-dx-live-03.bilivideo.com/live-bvc/171044/live_1701570_8782222_1500.flv?cdn=cn-gotcha01&expires=1620491825&len=0&oi=2102829853&pt=&qn=150&trid=eaa61ac6b47c4547bd46b79717509bbb&sigparams=cdn,expires,len,oi,pt,qn,trid&sign=00f4dfb037f4459585fc3a265b83dbfe&ptype=0&src=9&sl=1&sid=4309&sk=59b4112a8c653bb&order=2" 
                flvConfig={{enableWorker: true}}/>
            </div>
        );
    }
}
