import React, { Component } from 'react';
import { NavLink, Route, Redirect } from 'react-router-dom'

import AnimatedRouter from 'react-animated-router'

import Search from '../../page/Souman'

import 'react-animated-router/animate.css'
import './index.scss'

export default class index extends Component {

    state = {
        search: ""
    }

    inputChange = (event) => {
        this.setState({
            search: event.target.value
        })
    }

    render() {  
        return (
            <div>
                <Router>
                    <AnimatedRouter>
                        <Route exact path="/souman" render={ () => {
                            return(
                                <div className="souman-main-container">
                                    {/* <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/mikan/main' }}>首页</NavLink>
                                    <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/mikan/magent' }}>下载</NavLink>        */}
                                        <div className="container_mid">
                                            <div className="input-form">
                                                <input type="text" ref="search" onChange={ this.inputChange } />
                                                <NavLink className="main-form-btn" to={ `/souman/search/?key=${ this.state.search }` } >搜索</NavLink>
                                            </div>
                                        </div>
                                </div>
                            )
                        } }>

                        </Route>

                        <Route path="/souman/search" component={ Search }></Route>
                    </AnimatedRouter>
                </Router>
            </div>
        );
    }
}
