import React, { Component } from 'react';
import { NavLink, Route, Redirect,Switch } from 'react-router-dom'
import PubSub from 'pubsub-js';
import New from '../../page/New'
import Live from '../../page/Live'
import './index.scss'

export default class index extends Component {
    state = { info:"" }

    componentDidMount() {
        this.mysub = PubSub.subscribe('mytopc', (msg, data) => {
                this.setState({
                    info: data.cookie
                })
        })
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.mysub)
    }

    render() {  
        const {cookie} = this.state.info
        return (
            <div className="main-container">
            <div className="main-header">
             <div className="header-menu">
              <NavLink activeClassName="is-active" className="main-header-link" to="/New">最新</NavLink>
              <a className="main-header-link" href="/#" >{ cookie }</a>
              <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/Live',state: {cookie:cookie} }}>直播</NavLink>
             </div>
            </div>
            
            {/* switch包裹路由  设置默认匹配项 */}
            <Switch>
                <Route path="/New" component={ New } />
                <Route path="/Live" component={ Live } />
                <Redirect to="/New" />
            </Switch>
           </div>
        );
    }
}
