import React, { Component } from 'react';
import { NavLink, Route, Redirect } from 'react-router-dom'

import PubSub from 'pubsub-js';
import AnimatedRouter from 'react-animated-router'

import New from '../../page/New'
import Live from '../../page/Live'
import player from '../Player'
import Dynamic from '../../page/Dynamic'

import 'react-animated-router/animate.css'
import './index.scss'

export default class index extends Component {
    state = { info: "", userid: "" }

    componentDidMount() {
        this.mysub = PubSub.subscribe('mytopc', (msg, data) => {
                this.setState({
                    info: data.cookie,
                    userid: data.mid
                })
        })
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.mysub)
    }

    render() {  
        const  { cookie, userid }  = this.state.info
        return (
            <div className="main-container">
            <div className="main-header">
             <div className="header-menu">
              <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/New',state: {cookie: cookie} }}>最新</NavLink>
              <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/Dynamic', state: { cookie: cookie } }} >{ userid }</NavLink>
              <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/Live',state: {cookie: cookie} }}>直播</NavLink>
             </div>
            </div>
            
            {/* switch包裹路由  设置默认匹配项 */}
            <AnimatedRouter>
                <Route path="/New" component={ New } />
                <Route path="/Dynamic" component={ Dynamic } />
                <Route path="/Live" component={ Live } />
                <Route path="/Live/player" component={ player } />
                <Redirect to="/New" />
            </AnimatedRouter>
           </div>
        );
    }
}
