import axios from 'axios';
import React, { Component } from 'react';
import { NavLink, BrowserRouter as Router, Route } from 'react-router-dom';

import { Menu, Dropdown, Button } from 'antd'
import AnimatedRouter from 'react-animated-router'
import LongMenu from '../../../components/LongMenu';

import './index.scss'

export default class magent extends Component {

    state = {
        day : "",
        hour : "",
        second : "",
        sign : "",
        count : "",
        mid : "",
        page: "",
        pageSign: "",
        curl: "",
        pageNumber: 2
    }

    componentDidMount() {
        const _this = this
        let curl = this.props.location.search.slice(-6)
        axios.get('http://localhost:3001/api/firstinfo', {
            params: {
                curl
            }
        })
        .then( function(response) {
                let day = response.data.day
                let hour = response.data.hour
                let second = response.data.second
                let sign = response.data.result
                let count = response.data.count
                let mid = response.data.mid
            _this.setState({
                day: response.data.day,
                hour: response.data.hour,
                second: response.data.second,
                sign: response.data.result,
                count: response.data.count,
                mid: response.data.mid,
                curl: curl.slice(-5)
            })
            axios.get('http://localhost:3001/api/getpage',{
                params: {
                    curl,
                    mid,
                    day,
                    hour,
                    second,
                    sign,
                }
            })
            .then( function(response) {
                let page = response.data.page
                let pageSign = response.data.pageSign
                _this.setState({
                    page,
                    pageSign
                })
            })
            .catch( function(err) {
                console.log(err)
            })
        })
        .catch( function(err) {
            console.log(err)
        })
    }

    nextpage = (event) => {
        const _this = this
        const { curl, mid, day, hour, second, sign, pageNumber } = this.state
        console.log(pageNumber)
        axios.get('http://localhost:3001/api/getNumberpage', {
            params: {
                curl,
                mid,
                day,
                hour,
                second,
                sign,
                pageNumber
            }
        })
        .then(function(response) {
            _this.setState({
                page: response.data
            })
        })
        .catch(function(err) {
            console.log(err)
        })
        _this.setState({
            pageNumber: pageNumber + 1
        })
        let Node = document.getElementById("content")
        Node.scrollTop = 0
    }


    getpage = (page) => {
        const _this = this
        const { curl, mid, day, hour, second, sign } = this.state
        axios.get('http://localhost:3001/api/getNumberpage', {
            params: {
                curl,
                mid,
                day,
                hour,
                second,
                sign,
                page
            }
        })
        .then(function(response) {
            _this.setState({
                pageNumber: page,
                page: response.data
            })
        })
        .catch(function(err) {
            console.log(err)
        })
        let Node = document.getElementById("content")
        Node.scrollTop = 0
    }


    setClickpage = (i) => {
        console.log(i)
        // this.setState({
        //     pageNumber: i
        // })
        // this.getpage(i)
    }

    setpage = () => {
        const { count } = this.state
        let items = []
        for(var i = 1;i <= count;i++) {
            items.push(
                <Menu.Item>
                    <a target="_blank" rel="noopener noreferrer" onClick={ (i) => this.setClickpage(i) }>
                     { i }
                    </a>
                </Menu.Item>
            )
        }
        return items
    }

    render() {

        const { mid, curl, page, pageSign, count, pageNumber } = this.state

        const pages = (
            <Menu>
                  { 
                    this.setpage().map((item) => {
                        return(
                            item
                        )
                    }) 
                  }
            </Menu>
          )

        const chapter = (
            <Menu>
                  { 
                    this.setpage().map((item) => {
                        return(
                            item
                        )
                    }) 
                  }
            </Menu>
          )

        return (
            <div className="reader">

                <div className="content" id="content" onClick={ () => this.nextpage() }>
                    <img src={ `http://image.mangabz.com/1/${mid}/${curl}/${page}.jpg?cid=${curl}&key=${pageSign}=` } alt="" />
                </div>

                <div className="bottom">
                    <LongMenu count="54"/>
                    <img src="data:image/png;base64,"/>
                    {/* <Dropdown overlay={pages} placement="topCenter" arrow trigger={["click"]}>
                        <button>页码{ pageNumber }/{ count }</button>
                    </Dropdown>

                    <Dropdown overlay={chapter} placement="topCenter" arrow>
                        <button>章节1/200</button>
                    </Dropdown> */}
                    <LongMenu count="212"/>
                </div>

            </div>
        );
    }
}
