import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';

import './index.scss'
import axios from 'axios';

function PaperComponent(props) {
  return (
    <Draggable handle="#draggable-dialog-title" cancel={'[class*="MuiDialogContent-root"]'}>
      <Paper {...props} />
    </Draggable>
  );
}

export default function DraggableDialog(props) {
  const [open, setOpen] = React.useState(false)
  const [results, setResults] = React.useState([])
  const [subtitle, setSubtitle] = React.useState([])
  const [isShow, setisShow] = React.useState([true,false,false,false,false,false,false])
  const { imgSrc, name, id} = props

  const handleClickOpen = (id) => {
    setOpen(true);
    // axios.get('http://localhost:3001/api/moreinfo')
    // .then(function (response) {
    //   setResults(response.data)
    // })
    // .catch(function (err) {
    //   console.log(err)
    // })
    axios.get('http://localhost:3001/api/subtitle')
    .then(function (response) {
      setSubtitle(response.data)
    })
    .catch(function (err) {
      console.log(err)
    })
  }
  
  const handleClose = () => {
    setOpen(false);
  }

  const setShow = (number) => {
    let show = [false,false,false,false,false,false,false]
    show[number] = true
    setisShow(show)
  } 

  return (
    <div>
      <div style={{ display:"flex",height: "260px",width: "215px",justifyContent:"center" }} onClick={() => handleClickOpen(id)}>
        <img src={ imgSrc } />
      </div>
      <Dialog
        open={open}
        onClose={handleClose}
        PaperComponent={PaperComponent}
        aria-labelledby="draggable-dialog-title"
        maxWidth={false}
      >
        <DialogTitle style={{ cursor: 'move' }} id="draggable-dialog-title">
          { name }
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            <div className="content">
                <div className="right">

                  {
                    subtitle.map((left) => {
                      return(
                        <div className={ `name${index}` } onClick={() => setShow(1)}>
                          <button className={isShow[1] === true ? "custom-btn btn-6 isactive":"custom-btn btn-6"}>
                            <span>我是2</span>
                          </button>
                        </div>
                      )
                    })
                  }

                </div>
                <div className="mid">
                  <div style={isShow[0] === true ? {display:'block'}:{display:'none'}}>
                    <div className="container">
                      
                      <div className="items">
                        <div>
                          <span>[c.c动漫][4月新番][扰乱 THE PRINCESS OF SNOW AND BLOOD][07][简繁内挂][AVC_AAC][1080P][MKV] [162.11 MB]</span><button>[复制磁连]</button>
                        </div>
                        <p>
                          2021/05/26
                        </p>
                      </div>

                    </div>
                  </div>
                  <div style={isShow[1] === true ? {display:'block'}:{display:'none'}}>[c.c动漫][4月新番][扰乱 THE PRINCESS OF SNOW AND BLOOD][07][简繁内挂][AVC_AAC][1080P][MKV] [162.11 MB]</div>
                  <div style={isShow[2] === true ? {display:'block'}:{display:'none'}}>[c.c动漫][4月新番][扰乱 THE PRINCESS OF SNOW AND BLOOD][07][简繁内挂][AVC_AAC][1080P][MKV] [162.11 MB]</div>
                  <div style={isShow[3] === true ? {display:'block'}:{display:'none'}}>[c.c动漫][4月新番][扰乱 THE PRINCESS OF SNOW AND BLOOD][07][简繁内挂][AVC_AAC][1080P][MKV] [162.11 MB]</div>
                </div>
                <div className="left">
                  <img src={ imgSrc } />
                </div>
            </div>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            关 闭
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
