import axios from 'axios';
import React, { Component } from 'react';

import { CopyToClipboard } from 'react-copy-to-clipboard'
import { message } from 'antd'
import { localStorageSet, localStorageGet } from '../../../StorageGS'

import './index.scss'

export default class magent extends Component {

    state = {
        fixed: false,
        results: []
    }
    componentDidMount() {
        const _this = this

        // 为当前组件界面绑定滚动事件
        window.addEventListener('scroll',this.handleScroll, true)
        
        // 获取serach界面传过来的key值并进获取搜索结果
        let key = this.props.location.search.slice(5)
        if(key.search("%") == -1) {
            localStorageSet("key", key)
        }else {
            key = localStorageGet("key")
        }
        axios.get('http://localhost:3001/api/results', { params: {
            key
        } })
        .then(function (response) {
            let result = response.data.Items
            console.log(result)
            let array = []
            for (var key in result ) {
                array.push(result[key])
            }
            _this.setState({
                results: array
            })
        })
        .catch(function (err) {
            console.log(err)
        })

        
    }

    // 组件卸载时移除绑定事件
    componentWillUnmount() {
        window.removeEventListener('scroll',this.handleScroll)
    }

    // 绑定滚动事件调用函数
    handleScroll = (event) => {
        let _this = this
        let scrollTop = event.target.scrollTop
        if(scrollTop >= 120) {
            _this.setState({
                fixed: true
            })
        }else {
            _this.setState({
                fixed: false
            })
        }
    }

    render() {
        // 判断是否固定header
        let { fixed, results } = this.state
        let pltext = localStorageGet("key")
        let style = {}
        if(fixed) {
            style = { 
                position:"fixed",
                transition: "0.3s ease",
                backgroundColor:"rgba(255,255,255,0.95)",
                opacity: 0.95,
                margin: 0,
                padding: 0,
                borderBottom: "2px solid #ff6348",
                boxShadow: "0 10px 10px rgb(255 159 127 / 69%)"
            }
        }else {
            style = { 
            }
        }
        return (
            <div className="results-container"> 
                <div className="results-header" style={ style }>
                    <div className="input-form">
                        <input type="text" placeholder={ pltext } />
                        <button className="main-form-btn" to={ `/souman/search/?key=$` } >搜索</button>
                    </div>
                </div>
                <div className="results-main">
                    <div className="results-main_container">

                        {
                            // 渲染搜索出来的结果并遍历信息
                            results.map((infos) => {
                                return(
                                    <div className="results-item">
                                        {/* <img src= { infos.Comics[0].PicUrl } /> */}
                                        <div className="item-info">
                                            <p className="manga-item-title">
                                                <span>
                                                    { infos.Comics[0].Title }
                                                </span>
                                            </p>

                                            <p className="manga-item-line">
                                                <span>
                                                    作者: { infos.Comics[0].Author }
                                                </span>
                                            </p>

                                            <p className="manga-item-line">
                                                <span>
                                                    状态: { infos.Comics[0].Status }
                                                </span>
                                                <span>
                                                    更新至: { infos.Comics[0].LastPartName }
                                                </span>
                                            </p>

                                            <p className="manga-item-line">
                                                <span className="manga-content">
                                                    { infos.Comics[0].Content }
                                                </span>
                                            </p>

                                            <div className="type-block-con">
                                                {
                                                    // 渲染相关漫画资源条目
                                                    (infos.Comics).map((source, index) => {
                                                        return(
                                                            <span className="tab-con">
                                                                <a>{ source.Source }</a>
                                                            </span>
                                                        )
                                                    })
                                                }
                                            </div>

                                            <p className="manga-item-num">
                                                {
                                                     (infos.Comics[0].Chapters.length < 7) ? infos.Comics[0].Chapters.map((count => {
                                                            return(
                                                                <a>{ count.Name }</a>
                                                            )
                                                     })) : <div>
                                                         <a>{ infos.Comics[0].Chapters[0].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[1].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[2].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[3].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[4].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[5].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[6].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[7].Name }</a>
                                                         <a>. . .</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 9].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 8].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 7].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 6].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 5].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 4].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 3].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 2].Name }</a>
                                                         <a>{ infos.Comics[0].Chapters[infos.Comics[0].Chapters.length - 1].Name }</a>
                                                     </div>
                                                }
                                            </p>
                                        </div>
                                    </div>
                                )
                                // return(
                                //     (infos.Comics).map((info) => {
                                //         return(
                                            
                                //         )
                                //     })
                                // )
                            })
                        }

                    </div>
                </div>
            </div>
        );
    }
}
