import React, { Component } from 'react';
import axios from 'axios';

import { message, BackTop, Badge  } from 'antd'
import Dialog from '../../../components/Dialog'
import Catloading from '../../Loading'

import './index.scss'

export default class main extends Component {

    state = {
        isShow:false,
        loading: true,


        day1: "",
        results1: [],

        day2: "",
        results2: [],
        
        day3: "",
        results3: [],

        day4: "",
        results4: [],

        day5: "",
        results5: [],

        day6: "",
        results6: [],

        day7: "",
        results7: [],

        day8: "",
        results8: [],

        day9: "",
        results9: []
    }

