                                        </Badge>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>

                <div className="week">
                    <div className="week_top">
                        <span>{ day7 }</span>
                    </div>
                    <div className="week_main">
                        {
                            results7.map((info) => {
                                return(
                                    <div className="main-card__subtext">
                                        <Badge size="default" count={info.contentText}>
                                            <Dialog imgSrc = { info.img } name = { info.name } id = { info.id }>
                                        </Dialog>
                                        <div className="main-card-info">
                                            <span>{ info.time }</span>
                                            <span>{ info.name }</span>
                                        </div>
                                        </Badge>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>

                <div className="week">
                    <div className="week_top">
                        <span>{ day8 }</span>
                    </div>
                    <div className="week_main">
                        {
                            results8.map((info) => {
                                return(
                                    <div className="main-card__subtext">
                                        <Badge size="default" count={info.contentText}>
                                            <Dialog imgSrc = { info.img } name = { info.name } id = { info.id }>
                                        </Dialog>
                                        <div className="main-card-info">
                                            <span>{ info.time }</span>
                                            <span>{ info.name }</span>
                                        </div>
                                        </Badge>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>

                <div className="week">
                    <div className="week_top">
                        <span>{ day9 }</span>
                    </div>
                    <div className="week_main">
                        {
                            results9.map((info) => {
                                return(
                                    <div className="main-card__subtext">
                                        <Badge size="default" count={info.contentText}>
                                            <Dialog imgSrc = { info.img } name = { info.name } id = { info.id }>
                                        </Dialog>
                                        <div className="main-card-info">
                                            <span>{ info.time }</span>
                                            <span>{ info.name }</span>
                                        </div>
                                        </Badge>
                                    </div> 
                                )
                            })
                        }
                    </div>
                </div>

                {/* 渲染mikan上获取到的番剧信息 */}
                {/* {
                    results.map((info) => {
                        return(
                            <div className="main-card__subtext">
                                <img src={ info.img } />
                                <div className="main-card-info">
                                    <span>{ info.time }</span>
                                    <span>{ info.name }</span>
                                </div>
                            </div>
                        )
                    })
                } */}

                </div>
        );
    }
}
