import axios from 'axios';
import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

import { CopyToClipboard } from 'react-copy-to-clipboard'
import { message } from 'antd'

import './index.scss'

export default class magent extends Component {

    state = {
        search: ""
    }

    inputChange = (event) => {
        this.setState({
            search: event.target.value
        })
    }

    componentDidMount() {
        const _this = this
        axios.get("http://localhost:3001/api/magent")
        .then(function (response) {
            _this.setState({
                magents: response.data
            })   
        })
        .catch(function (err) {
            console.log(err)
        })
    }

    render() {
        const { magents } = this.state

        return (
            <div className="souman-main-container">
            {/* <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/mikan/main' }}>首页</NavLink>
            <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/mikan/magent' }}>下载</NavLink>        */}
                <div className="container_mid">
                    <div className="input-form">
                        <input type="text" ref="search" onChange={ this.inputChange } />
                        <NavLink className="main-form-btn" to={ `/souman/search/?key=${ this.state.search }` } >搜索</NavLink>
                    </div>
                </div>
            </div>
        );
    }
}
