import axios from 'axios';
import React, { Component } from 'react';

import { CopyToClipboard } from 'react-copy-to-clipboard'
import { message } from 'antd'
import { localStorageSet, localStorageGet } from '../../../StorageGS'

import './index.scss'

export default class magent extends Component {

    state = {
        fixed: false
    }
    componentDidMount() {
        const _this = this

        // 为当前组件界面绑定滚动事件
        window.addEventListener('scroll',this.handleScroll, true)
        
        // 获取serach界面传过来的key值并进获取搜索结果
        let key = this.props.location.search.slice(5)
        console.log(key)
        if(key.search("%") == -1) {
            localStorageSet("key", key)
        }
        axios.get('http://localhost:3001/api/results', { params: {
            key
        } })
        .then(function (response) {
            console.log(response)
        })
        .catch(function (err) {
            console.log(err)
        })

        
    }

    // 组件卸载时移除绑定事件
    componentWillUnmount() {
        window.removeEventListener('scroll',this.handleScroll)
    }

    // 绑定滚动事件调用函数
    handleScroll = (event) => {
        let _this = this
        let scrollTop = event.target.scrollTop
        if(scrollTop >= 120) {
            _this.setState({
                fixed: true
            })
        }else {
            _this.setState({
                fixed: false
            })
        }
    }

    render() {
        // 判断是否固定header
        let { fixed } = this.state
        let pltext = localStorageGet("key")
        let style = {}
        if(fixed) {
            style = { 
                position:"fixed",
                transition: "0.3s ease",
                backgroundColor:"#ffffff",
                opacity: 0.95,
                margin: 0,
                padding: 0,
                borderBottom: "2px solid #ff6348",
                boxShadow: "0px  0px 60px 0px rgb(255 159 127 / 69%)"
            }
        }else {
            style = { 
            }
        }
        return (
            <div className="results-container"> 
                <div className="results-header" style={ style }>
                    <div className="input-form">
                        <input type="text" placeholder={ pltext } />
                        <button className="main-form-btn" to={ `/souman/search/?key=$` } >搜索</button>
                    </div>
                </div>
                <div className="main"></div>
            </div>
        );
    }
}
