import React, { Component } from 'react';

import { localStorageGet, localStorageSet } from '../../../StorageGS';

import './index.scss'

export default class index extends Component {

    state = {
        isShow: false,
        searchHistory: [],
        searchInfo: ''
    }

    componentDidMount() {
        if(localStorageGet("searchHistory") !== null) {
            this.setState({
                searchHistory: JSON.parse(localStorageGet("searchHistory"))
            })
        }
        // let script2 = document.createElement('script')
        // script2.type = 'text/javascript'
        // script2.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/1.16.1/TimelineLite.min.js'
        // document.getElementById('root').appendChild(script2)

        // let script3 = document.createElement('script')
        // script3.type = 'text/javascript'
        // script3.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/1.16.1/TweenLite.min.js'
        // document.getElementById('root').appendChild(script3)

        // let script4 = document.createElement('script')
        // script4.type = 'text/javascript'
        // script4.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/1.16.1/TweenMax.min.js'
        // document.getElementById('root').appendChild(script4)

        // let script = document.createElement('script')
        // script.type = 'text/javascript'
        // script.src = 'http://81.71.103.97/swiper/search.js'
        // document.getElementById('root').appendChild(script)

    }

    // 打开历史搜索记录
    showSearchhistory = () => {
        this.setState({
            isShow: true
        })
    }

    // 关闭历史搜索记录
    closeSearchhistory = () => {
        this.setState({
            isShow: false
        })
    }

    // 点击搜索  存储搜索记录
    Search = () => {
        let {searchHistory, searchInfo} = this.state
        if(searchInfo !== ''){
            if(searchHistory.length === 10) {
                searchHistory.pop()
            }
            localStorageSet("searchHistory", JSON.stringify([searchInfo,...searchHistory]))
        }
    }

    // 将搜索词条保存本地
    inputChange = (e) => {
        this.setState({
            searchInfo: e.target.value
        })
    }

    // 删除指定搜索信息
    deleHistory = (index) => {
        let searchHistory = this.state.searchHistory
        searchHistory.splice(index, 1)
        this.setState({
            searchHistory 
        })
        localStorageSet("searchHistory", JSON.stringify(searchHistory))
    }

    render() {
        const {isShow, searchHistory} = this.state

        return (
            <div className="search">
                <div className="search-mask" style={isShow ? {display:'block'} : {display:'none'}}></div>
                {/* 伸缩搜索框
                <form class="search">
                    <input class="search-input" id="input" type="text" autocomplete="off"/>
                    <label class="search-label" for="input"></label>
                    <button class="search-button" type="reset"></button>
                    <div class="search-border"></div>
                    <div class="search-btn">搜索</div>
                    <div class="search-close">x</div>
                </form> */}

                <div className="nav-search-box">
                    <form id="nav_searchform">
                        <input className="nav-search-keyword" onChange={ (e) => this.inputChange(e) } onBlur={ this.closeSearchhistory }  onFocus={ this.showSearchhistory } type="text" />
                        <div className="nav-search-btn">
                            <button type="button" className="bilifont bili-icon_dingdao_sousuo nav-search-submit" onClick={ () => this.Search() }></button>
                        </div>
                    </form>
                    <ul id="bilibili-search-history" style={isShow && searchHistory !== [] ? {display:'block'} : {display:'none'}} className="bilibili-search-history report-wrap-module history">
                        {
                            (searchHistory || []).map((item, index) => {
                                return(
                                    <li className="history-item">
                                        <a>{ item }</a>
                                        <i className="bilifont bili-icon_sousuo_yichu cancel-icon" onClick={ () => this.deleHistory(index) }></i>
                                    </li>
                                )
                            })
                        }
                    </ul>
                </div>
            </div>
        )
    }
}
