import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';

import './index.scss'
import axios from 'axios';

function PaperComponent(props) {
  return (
    <Draggable handle="#draggable-dialog-title" cancel={'[class*="MuiDialogContent-root"]'}>
      <Paper {...props} />
    </Draggable>
  );
}

export default function DraggableDialog(props) {
  const [open, setOpen] = React.useState(false)
  const [results, setResults] = React.useState([])
  const [isShow, setisShow] = React.useState([false,false,false,false,false,false,false])
  const { imgSrc, name, id} = props

  const handleClickOpen = (id) => {
    setOpen(true);
    // axios.get('http://localhost:3001/api/moreinfo')
    // .then(function (response) {
    //   setResults(response.data)
    // })
    // .catch(function (err) {
    //   console.log(err)
    // })
  }
  
  const handleClose = () => {
    setOpen(false);
  }

  const setShow = (number) => {
    setisShow(isShow[true,false,false,false,false,false,false])
    setisShow(isShow[number] = true)
  } 

  return (
    <div>
      <div style={{ display:"flex",height: "260px",width: "215px",justifyContent:"center" }} onClick={() => handleClickOpen(id)}>
        <img src={ imgSrc } />
      </div>
      <Dialog
        open={open}
        onClose={handleClose}
        PaperComponent={PaperComponent}
        aria-labelledby="draggable-dialog-title"
        maxWidth={false}
      >
        <DialogTitle style={{ cursor: 'move' }} id="draggable-dialog-title">
          { name }
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            <div className="content">
                <div className="right">
                  <div className="name1" onClick={() => setShow(1)} style={ isShow[1] ===true ? {display:'block'} : { display: 'none' }  }>1</div>
                  <div className="2" onClick={() => setShow(2)} style={ isShow[2] ===true ? {display:'block'} : { display: 'none' }  }>2</div>
                  <div className="3" onClick={() => setShow(3)} style={ isShow[3] ===true ? {display:'block'} : { display: 'none' }  }>3</div>
                  <div className="4" onClick={() => setShow(4)} style={ isShow[4] ===true ? {display:'block'} : { display: 'none' }  }>4</div>
                </div>
                <div className="mid">
                </div>
                <div className="left">
                  <img src={ imgSrc } />
                </div>
            </div>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            关 闭
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
