var CONTROLBAR_HIDE_TIME = exports.CONTROLBAR_HIDE_TIME = 2000;
var DEFAULT_PLAYBACKRATES = exports.DEFAULT_PLAYBACKRATES = [1, 1.25, 1.5, 1.75, 2];
