import React, { Component } from 'react';
import qs from 'query-string'
import axios from 'axios';

import Html5Player from 'html5-player'
import LMPlayer from 'lm-player'
import { ReactFlvPlayer } from 'react-flv-player'
import ReactPlayer from 'react-player'

import { Button, PageHeader, Drawer } from 'antd';
import { localStorageGet, localStorageSet } from '../../StorageGS';

import 'html5-player/libs/assets/css/style.css'
import './index.scss'

export default class index extends Component {
    state = {
        nowurl: "",
        nowbv: "",
        nowcid: "",
        all: [],
        visible: false
    }

    // 接受参数
    componentDidMount(){
        const _this = this
        let { cookie, season_id, Episode } = this.props.location.state
        if( cookie !== "" ) {
            localStorageSet("cookie",cookie)
        }else {
            cookie = localStorageGet("cookie")
        }
        // 获取点进番剧的信息
        axios.get("http://localhost:3001/api/seasoninfo", {
            params: { season_id }
        })
        .then ( function (response) {
            _this.setState({
                all: response.data
            })
            let result = response.data
            console.log(result)
            for (var i = 0;i < result.length; i++) {
                if (Number(result[i].title) === Number(Episode)) {
                    let nowbv = result[i].bvid
                    let nowcid = result[i].cid
                    _this.setState({
                        nowbv,
                        nowcid
                    })
                    
                    axios.get("http://localhost:3001/api/clickvideo", {
                        params: {
                            nowbv,
                            nowcid,
                            cookie
                        }
                    })
                    .then( function (response) {
                        _this.setState({
                            nowurl: response.data.data.durl[0].url
                        })
                    })
                    .catch( function (err) {
                        console.log(err)
                    })
                }
            }
        })
        .catch ( function (err) {
            console.log(err)
        })     
    }

    showDrawer = () => {
        this.setState({
          visible: true,
        });
      };
    
      onClose = () => {
        this.setState({
          visible: false,
        });
      };

      getContainer = () => {
        return document.getElementById('video-container-top');
      };

    render() {
        const { nowurl, all } = this.state
        return (
            <div className="video-container">
                <div id="video-container-top">
                    <PageHeader
                        className="site-page-header"
                        onBack={() => this.props.history.go(-1)}
                        title="返回"
                        subTitle=""                    
                    />
                    <Button onClick={ this.showDrawer }>open</Button>

                    <Drawer
                    placement="right"
                    closable={false}
                    maskClosable={true}
                    onClose={this.onClose}
                    visible={this.state.visible}
                    getContainer={"#video-container-top"}
                    style={{ position: 'absolute' }}
                    >
                        {
                            all.map((fanju) => {
                                <div>
                                    <p>{ fanju.title }</p>
                                </div>
                            })
                        }

                    </Drawer>
                </div>
                
                {/* 播放器一  卡顿 */}
                <Html5Player
                file={ nowurl }
                autoplay={ false }
                />

                {/* 播放器二  卡顿 */}
                {/* <LMPlayer 
                file={ nowurl }
                muted="false"
                autoplay={ true }
                /> */}

                {/* 播放器三  卡顿*/}
                                
                {/* <ReactFlvPlayer
                url={ nowurl }
                heigh="800px"
                width="800px"
                /> */}

                
                {/* <ReactPlayer 
                url={ nowurl }
                width="80%"
                height="80%"
                playing={true}
                controls
                /> */}



            </div>
        );
    }
}
