import React, { Component } from 'react';
import { Drawer, Button, Radio, Space } from 'antd'
import './index.scss'

export default class index extends Component {

    state = { visible: false, placement: 'top' }

    showDrawer = () => {
        this.setState({
            visible: true,
        })
    }

    onClose = () => {
        this.setState({
            visible: false,
        })
    }
    
    render() {
        const {placement, visible } = this.state
        return (
            <div>
                <div className="header">
                    <div className="menu-circle"></div>
                    <div className="header-menu">
                        <a className="menu-link is-active" href="/#">网站一</a>
                        <a className="menu-link notify" href="/#">网站二</a>
                        <a className="menu-link" href="/#">网站三</a>
                        <a className="menu-link notify" href="/#">网站四</a>
                    </div>
                    <div className="search-bar">
                        <input type="text" placeholder="Search" />
                    </div>
                    <div className="header-profile">
                    <div className="notification">
                        <span className="notification-number"></span>
                        <svg viewBox="0 0 24 24" fill="currentColor" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-bell">
                        <path d="M18 8A6 6 0 006 8c0 7-3 9-3 9h18s-3-2-3-9M13.73 21a2 2 0 01-3.46 0" />
                        </svg>
                    </div>
                    <svg viewBox="0 0 512 512" fill="currentColor">
                        <path d="M448.773 235.551A135.893 135.893 0 00451 211c0-74.443-60.557-135-135-135-47.52 0-91.567 25.313-115.766 65.537-32.666-10.59-66.182-6.049-93.794 12.979-27.612 19.013-44.092 49.116-45.425 82.031C24.716 253.788 0 290.497 0 331c0 7.031 1.703 13.887 3.006 20.537l.015.015C12.719 400.492 56.034 436 106 436h300c57.891 0 106-47.109 106-105 0-40.942-25.053-77.798-63.227-95.449z" />
                    </svg>

                    <Drawer
                    title="Basic Drawer"
                    placement={placement}
                    closable={false}
                    onClose={this.onClose}
                    visible={visible}
                    key={placement}
                    >
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                        <p>Some contents...</p>
                    </Drawer>
                    
                    <div className="img-div" onClick={ this.showDrawer }>
                        <img className="profile-img" src="https://ae01.alicdn.com/kf/U5ab2f5b2838d4f5ba21f8b6402ba4c17P.jpg" alt="" />
                    </div>
                    </div>
                </div>
            </div>
        );
    }
}
