import React, { Component } from 'react';
import axios from 'axios';

import { message, BackTop } from 'antd'

import './index.scss'

export default class main extends Component {

    state = {
        results:{}
    }

    componentDidMount() {
        const _this = this
        axios.get("http://localhost:3001/api/main")
        .then( function (response) {
            console.log(response.data[0])
            _this.setState({
                results: response.data[0].data
            })
        })
        .catch( function (err) {
            console.log(err)
        })
    }

    render() {
        const result  = this.state.results
        const infos = this.state.results.results
        const style = {
            height: 40,
            width: 40,
            lineHeight: '40px',
            borderRadius: 4,
            backgroundColor: '#1088e9',
            color: '#fff',
            textAlign: 'center',
            fontSize: 14,
          };

        return (
            <div className="main-card-container" id="main-card-container">

            　　<BackTop visibilityHeight={300} target ={()=>document.getElementById('main-card-container')} >
            　　　　<div className="backtopbtn"  style={style}>UP</div>
            　　</BackTop>

                <div className="week">
                    <div className="week_top">
                        <span>{ result.day }</span>
                    </div>
                    <div className="week_main">
                        {
                            infos.map((info) => {
                                return(
                                    <div className="main-card__subtext">
                                        <img src={ info.img } />
                                        <div className="main-card-info">
                                            <span>{ info.time }</span>
                                            <span>{ info.name }</span>
                                        </div>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>

                {/* 渲染mikan上获取到的番剧信息 */}
                {/* {
                    results.map((info) => {
                        return(
                            <div className="main-card__subtext">
                                <img src={ info.img } />
                                <div className="main-card-info">
                                    <span>{ info.time }</span>
                                    <span>{ info.name }</span>
                                </div>
                            </div>
                        )
                    })
                } */}

                </div>
        );
    }
}
