import React, { Component } from 'react';
import { NavLink, Route, Redirect,Switch } from 'react-router-dom'
import New from '../../page/New'
import Live from '../../page/Live'
import './index.scss'

export default class index extends Component {

    change = () => {
        PubSub.pulish('mytopc', {name: 'li'})
    }

    render() {  
        return (
            <div className="main-container">
            <div className="main-header">
             <div className="header-menu">
              <NavLink activeClassName="is-active" className="main-header-link" to="/New">最新</NavLink>
              <a className="main-header-link" href="/#" onClick={ this.change }>订阅</a>
              <NavLink activeClassName="is-active" className="main-header-link" to="/Live">直播</NavLink>
             </div>
            </div>
            
            {/* switch包裹路由  设置默认匹配项 */}
            <Switch>
                <Route path="/New" component={ New } />
                <Route path="/Live" component={ Live } />
                <Redirect to="/New" />
            </Switch>
           </div>
        );
    }
}
