import axios from 'axios';
import React, { Component } from 'react';
import { NavLink, BrowserRouter as Router, Route } from 'react-router-dom';

import { Drawer, Button } from 'antd'
import AnimatedRouter from 'react-animated-router'
import { CopyToClipboard } from 'react-copy-to-clipboard'
import { message } from 'antd'

import './index.scss'

export default class magent extends Component {

    state = {
        day : "",
        hour : "",
        second : "",
        sign : "",
        count : "",
        mid : "",
        page: "",
        pageSign: "",
        curl: "",
        pageNumber: 2,
        visible: false
    }

    componentDidMount() {
        const _this = this
        let curl = this.props.location.search.slice(-6)
        axios.get('http://localhost:3001/api/firstinfo', {
            params: {
                curl
            }
        })
        .then( function(response) {
                let day = response.data.day
                let hour = response.data.hour
                let second = response.data.second
                let sign = response.data.result
                let count = response.data.count
                let mid = response.data.mid
            _this.setState({
                day: response.data.day,
                hour: response.data.hour,
                second: response.data.second,
                sign: response.data.result,
                count: response.data.count,
                mid: response.data.mid,
                curl: curl.slice(-5)
            })
            axios.get('http://localhost:3001/api/getpage',{
                params: {
                    curl,
                    mid,
                    day,
                    hour,
                    second,
                    sign,
                }
            })
            .then( function(response) {
                let page = response.data.page
                let pageSign = response.data.pageSign
                _this.setState({
                    page,
                    pageSign
                })
            })
            .catch( function(err) {
                console.log(err)
            })
        })
        .catch( function(err) {
            console.log(err)
        })
    }

    nextpage = (event) => {
        const _this = this
        const { curl, mid, day, hour, second, sign, pageNumber } = this.state
        console.log(pageNumber)
        axios.get('http://localhost:3001/api/getNumberpage', {
            params: {
                curl,
                mid,
                day,
                hour,
                second,
                sign,
                pageNumber
            }
        })
        .then(function(response) {
            _this.setState({
                page: response.data
            })
        })
        .catch(function(err) {
            console.log(err)
        })
        _this.setState({
            pageNumber: pageNumber + 1
        })
        let Node = document.getElementById("content")
        Node.scrollTop = 0
    }


    showDrawer = () => {
        this.setState({
          visible: true,
        })
    }

    onClose = () => {
        this.setState({
          visible: false,
        })
    }

    render() {

        const { mid, curl, page, pageSign } = this.state

        const style = {
            position: "relative",
            height: "100%",
            width: "100px",
            padding: "48px",
            overflow: "hidden",
            background: "#fafafa",
            border: "1px solid #ebedf0",
        }

        return (
            <div className="reader">

                <div className="site-drawer-render-in-current-wrapper" style={ style }>
                        Render in this
                        <div style={{ marginTop: 16 }}>
                        <Button type="primary" onClick={this.showDrawer}>
                            Open
                        </Button>
                        </div>
                        <Drawer
                        placement="left"
                        closable={false}
                        onClose={this.onClose}
                        visible={this.state.visible}
                        getContainer={false}
                        style={{ position: 'absolute' }}
                        >
                        </Drawer>
                    </div>

                <div className="content" id="content" onClick={ () => this.nextpage() }>
                    <img src={ `http://image.mangabz.com/1/${mid}/${curl}/${page}.jpg?cid=${curl}&key=${pageSign}=` } alt="" />
                </div>
                
            </div>
        );
    }
}
