import React, { Component } from 'react';
import { NavLink, Route, Redirect } from 'react-router-dom'

import PubSub from 'pubsub-js';
import AnimatedRouter from 'react-animated-router'

import New from '../../page/New'
import Live from '../../page/Live'
import player from '../Player'
import Dynamic from '../../page/Dynamic'

import 'react-animated-router/animate.css'
import './index.scss'

export default class index extends Component {
    state = { cookie: "", userid: "" }

    componentDidMount() {
        this.mysub = PubSub.subscribe('mytopc', (msg, data) => {
                this.setState({
                    cookie: data.cookie,
                    userid: data.mid
                })
        })
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.mysub)
    }

    render() {  
        const  { cookie, userid }  = this.state
        return (
            <div className="main-container">
            <div className="main-header">
             <div className="header-menu">
              <NavLink activeClassName="is-active" className="main-header-link" to={{ pathname: '/mikan/magent',state: {cookie} }}>下载</NavLink>     
             </div>
            </div>
            
            {/* switch包裹路由  设置默认匹配项 */}
            <AnimatedRouter>
                <Route exact path="/mikan/magent" component={ New } />
                <Redirect to="/mikan/magent" />
            </AnimatedRouter>
           </div>
        );
    }
}
