import React, { Component } from 'react';
import qs from 'query-string'
import axios from 'axios';

import Html5Player from 'html5-player'

import { localStorageGet, localStorageSet } from '../../StorageGS';

import 'html5-player/libs/assets/css/style.css'
import './index.scss'

export default class index extends Component {
    state = {
        liveurl: ""
    }

    // 接受参数
    componentDidMount(){
        const _this = this
        let id = qs.parse(this.props.location.search).id
        if (id !== "") {
            localStorageSet("liveId",id)
        } else {
            id = localStorageGet("liveId")
        }
        axios.get("http://localhost:3001/api/liveurl", {
            params: { id: localStorageGet("liveId") }
        })
        .then (function (response) {
            _this.setState({
                liveurl: response.data.result.data.durl[3].url
            })
        })
        .catch (function (err) {
            console.log(err)
        })
    }

    render() {
        let liveurl = this.state.liveurl
        return (
            <div className="vedio-container">
                <Html5Player className="player"
                flvConfig={{enableWorker: true}}
                isLiving={ true }
                livingMaxBuffer={ 5.0 }
                width="900px"
                height="560px"
                file={ liveurl }
                autoplay={ true }
                />
            </div>
        );
    }
}
