import axios from 'axios';
import React, { Component } from 'react';
import { NavLink, Route, BrowserRouter as Router } from 'react-router-dom';

import AnimatedRouter from 'react-animated-router'

import player from '../../../components/PlayerFanju'
import { localStorageGet, localStorageSet } from '../../../StorageGS';

import './index.scss'

export default class index extends Component {

    state = {
        latest:[],
        cookie:""
    }
    componentDidMount() {
        // 通过路由拿到登录页面cookie
        const _this = this
        let cookie = ""
        if ( this.props.location.state !== null ) {
            if ( this.props.location.state === undefined ) {
                cookie = " "
            }else {
                if ( this.props.location.state.cookie === "") {
                    cookie = localStorageGet("cookie",cookie)
                    this.setState({
                        cookie
                    })
                }
                else {
                    localStorageSet("cookie",this.props.location.state.cookie)
                    cookie = localStorageGet("cookie")
                    this.setState({
                        cookie
                    })
                }
            }
        } else {
            cookie = localStorageGet("cookie")
            this.setState({
                cookie
            })
        }

        // 获取季度番信息
        axios.get("http://localhost:3001/api/season")
        .then(function (response){
            _this.setState({
                latest: response.data.result.latest
            })
        })
        .catch(function (err) {
            console.log(err)
        })
    }
    
    render() {
        const { latest, cookie } = this.state
        return (
            <div>
                {/* 多级嵌套路由渲染最新页面或者播放页面 */}

                <Router>

                    <AnimatedRouter>
                        <Route exact path="/bilibili/New" render={ () => {
                            return(
                                <div className="content-wrapper">
                                    

                                <div className="content-wrapper-header" style={{ backgroundImage:"url(https://ae01.alicdn.com/kf/U1f18ea1afa764e1e83b8222106d3bdffL.jpg)" }} >
                                </div> 
                                 <div className="content-section">
                                  <div className="content-section-title">更新与热门</div>
                                  <div className="apps-card">
                                   
                                    {
                                        latest.map((videoinfo) => {
                                            return (
                                                <div className="app-card" key={ videoinfo.season_id }>
                                                    <div className="app-card-top">
                                                        <span className="latestVideo">
                                                            <img src={ videoinfo.square_cover } width="90px" height="123px" alt="" />
                                                        </span>
                                                        <div className="app-card__subtext">
                                                            <span>{ videoinfo.title }</span>
                                                            <span>{ videoinfo.pub_index }</span>
                                                        </div>
                                                    </div>
                                                    <div className="app-card-buttons">
                                                        <NavLink activeClassName="status-button" className="content-button" to={{ pathname: '/bilibili/New/player',state: {cookie: cookie, season_id: videoinfo.season_id, Episode: videoinfo.pub_index.slice(1,-1)} }}>进入观看</NavLink>
                                                        <div className="menu"></div>
                                                    </div>
                                                </div>
                                            )
                                        })
                                    }
                                  </div>
                                 </div>
                                </div>
                            )
                        }}></Route>

                        <Route path="/bilibili/New/player" component={ player }></Route>
                    </AnimatedRouter>

                </Router>

            </div>
        );
    }
}
