import React, { Component } from 'react';

import './index.scss'

export default class index extends Component {
    state = {
        isActive: 1
    }

    setActive = (number) => {
        alert(number)
    }
    render() {
        const { isActive } = this.state

        return (
            <div className="hpoi-container">
                <div className="box-title">
                    <ul className="hpoi-switch-header">
                        <li className="hpoi-switcher-header-item" style={ isActive === 1 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(1) }>全部</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 2 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(2) }>制作</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 3 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(3) }>更图</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 4 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(4) }>开订</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 5 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(5) }>延期</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 6 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(6) }>出荷</li>
                        <li className="hpoi-switcher-header-item" style={ isActive === 7 ? { borderBottom: '2px solid #00a1d6' } : {} } onClick={ () => this.setActive(7) }>再版</li>
                    </ul>
                </div>
            </div>
        )
    }
}
