import React, { Component } from 'react';
import qs from 'query-string'
import axios from 'axios';

import Html5Player from 'html5-player'
import LMPlayer from 'lm-player'
import { ReactFlvPlayer } from 'react-flv-player'

import { localStorageGet, localStorageSet } from '../../StorageGS';

import 'html5-player/libs/assets/css/style.css'
import './index.scss'

export default class index extends Component {
    state = {
        liveurl: ""
    }

    // 接受参数
    componentDidMount(){
        const _this = this
        let id = qs.parse(this.props.location.search).id
        if (id !== "") {
            localStorageSet("liveId",id)
        } else {
            id = localStorageGet("liveId")
        }
        axios.get("http://localhost:3001/api/liveurl", {
            params: { id: localStorageGet("liveId") }
        })
        .then (function (response) {
            _this.setState({
                liveurl: response.data.result.data.durl[2].url
            })
        })
        .catch (function (err) {
            console.log(err)
        })
    }

    render() {
        let liveurl = this.state.liveurl
        return (
            <div className="vedio-container">
                {/* <Html5Player className="player"
                flvConfig={{enableWorker: true}}
                livingMaxBuffer={ 15 }
                file={ liveurl }
                autoplay={ true }
                /> */}

                {/* <LMPlayer 
                file={ liveurl }
                isLive={ true }
                muted="false"
                autoplay={ true }
                /> */}

                
                <ReactFlvPlayer
                url={ liveurl }
                heigh="800px"
                width="800px"
                isLive="true"
                enableStashBuffer={false}
                isMuted={false}
                />

            </div>
        );
    }
}
