import axios from 'axios';
import React, { Component } from 'react';
import { NavLink, Route, Switch, Redirect } from 'react-router-dom';
import './index.scss';
import { localStorageGet,localStorageSet } from '../../StorageGS';
import player from '../../components/Player'

export default class index extends Component {
    
    state = {
        liveItems: []
    }

    componentDidMount(){
        // 通过路由拿到登录页面cookie访问关注主播信息
        const _this = this
        console.log(this.props)
        let cookie = this.props.location.state.cookie;
        if (cookie !== "") {
            localStorageSet("cookie",cookie)
        } else {
            cookie = localStorageGet("cookie")
        }
        axios.get("http://localhost:3001/api/list", { params: { cookie } })
        .then(function (response) {
            _this.setState({
                liveItems: response.data.result.data.items
            })
        })
        .catch(function (err) {
            console.log(err)
        })
    }
    
    render() {
        const liveItems = this.state.liveItems
        let liveShow
        // 渲染关注主播的房间
        if ( liveItems !== [] ) {
            liveShow = (
                (liveItems || []).map((liver) => {
                    return (
                        <div className="app-card" key={ liver.uid }>
                            <div className="app-card-top">
                                <span>
                                    <img src={ `https://images.weserv.nl/?url=${liver.face}` } width="90px" height="123px" alt="" />
                                </span>
                                <div className="app-card__subtext">
                                    <span>{ liver.title }</span>
                                    <span>{ liver.uname }</span>
                                </div>
                            </div>
                            <div className="app-card-buttons">
                            <NavLink className="content-button status-button" to="/Live/player">进入</NavLink>
                            <div className="menu"></div>
                            </div>
                        </div>
                    )
                })
            )
        } else {
            liveShow = (<div></div>)
        }

        return (
                <Switch>
                    <Route exact to="/Live/player" component={ player } />
                    <Redirect to="/Live"/>
                </Switch>

        );
    }
}
